# –û—Ç—Ä–∞–∂–µ–Ω–∏–µ –•–∞—É—Å—Ö–æ–ª–¥–µ—Ä–∞:
def householder(x):
  e1 = np.zeros_like(x)
  e1[0] = 1

  # –≤—ã—á–∏—Å–ª–µ–Ω–∏–µ alpha
  alpha = np.sign(x[0])*np.sqrt(np.sum(x**2))

  # –º–∞—Ç—Ä–∏—Ü–∞ –æ—Ç—Ä–∞–∂–µ–Ω–∏—è u
  u = x - alpha*e1
  # –Ω–æ—Ä–º–∞–ª–∏–∑–∞—Ü–∏—è u
  norm_u = np.sum(u**2)

  if norm_u > 1e-10:
    u = u/np.sqrt(norm_u)
  else:
    u = np.zeros_like(u)


  return u

# QR-—Ä–∞–∑–ª–æ–∂–µ–Ω–∏–µ
def qr_dec(A):
  n, m = A.shape

  # –µ–¥–∏–Ω–∏—á–Ω–∞—è –º–∞—Ç—Ä–∏—Ü–∞ Q
  Q = np.eye(n)
  R = A.copy()

  # –∏—Ç–∞—Ä–∏—Ü–∏—è —Ä–∞–∑–ª–æ–∂–µ–Ω–∏—è
