#pragma once
#include <random>

/// code generated by ChatGPT, as this function is weird and not something that
/// I need to understand rn.
class RandomNumberGenerator {
public:
  // Constructor that takes an integer seed
  explicit RandomNumberGenerator(int seed) : gen(seed), dis(-1.0, 1.0) {}

  // Method to generate a random double between -1 and 1
  double generate() {
    double val = dis(gen);
    if (val > 1.0) {
      return 1.0;
    }
    if (val < -1.0) {
      return -1.0;
    }
    return val;
  }

private:
  std::mt19937 gen; // Mersenne Twister random number generator
  std::uniform_real_distribution<double>
      dis; // Uniform distribution in the range [-1, 1]
};
